(5757, 363, 'editorial_content_wk_3_title_e_wk', '<span class=\"yellow-text\"> Practical</span>  Experience'),
(5758, 363, '_editorial_content_wk_3_title_e_wk', 'field_65bfc29851892'),
(5759, 363, 'editorial_content_wk_3_description_e_wk', 'The world of cybersecurity demands practical skills. Kyureeus provides hands-on, experiential learning opportunities that allow our students to gain invaluable insights and practical experience. This approach prepares them for the demands of the industry from day one.'),
(5760, 363, '_editorial_content_wk_3_description_e_wk', 'field_65bfc2a751893'),
(5761, 363, 'editorial_content_wk_3_image_editorial_wk', '341'),
(5762, 363, '_editorial_content_wk_3_image_editorial_wk', 'field_65bfc2d151894'),
(5763, 363, 'editorial_content_wk_3_theme_wk_editorial', 'dark'),
(5764, 363, '_editorial_content_wk_3_theme_wk_editorial', 'field_65bfc2ec51895'),
(5765, 363, 'editorial_content_wk_4_title_e_wk', 'Prepared for <span class=\"purple-text\">Challenges</span>'),
(5766, 363, '_editorial_content_wk_4_title_e_wk', 'field_65bfc29851892'),
(5767, 363, 'editorial_content_wk_4_description_e_wk', 'Kyureeus graduates are not just knowledgeable; they are highly skilled professionals capable of tackling the most pressing cybersecurity challenges. Our programs instill a problem-solving mindset, ensuring that our students can adapt and respond effectively to the ever-evolving cybersecurity landscape.'),
(5768, 363, '_editorial_content_wk_4_description_e_wk', 'field_65bfc2a751893'),
(5769, 363, 'editorial_content_wk_4_image_editorial_wk', '342'),
(5770, 363, '_editorial_content_wk_4_image_editorial_wk', 'field_65bfc2d151894'),
(5771, 363, 'editorial_content_wk_4_theme_wk_editorial', 'white'),
(5772, 363, '_editorial_content_wk_4_theme_wk_editorial', 'field_65bfc2ec51895'),
(5773, 363, 'editorial_content_wk_5_title_e_wk', 'Dedication to <span class=\"yellow-text\">Excellence</span>'),
(5774, 363, '_editorial_content_wk_5_title_e_wk', 'field_65bfc29851892'),
(5775, 363, 'editorial_content_wk_5_description_e_wk', 'We are committed to excellence in cybersecurity education. Our instructors, curriculum designers, and support staff work tirelessly to provide a learning environment that fosters innovation, critical thinking, and a deep understanding of cybersecurity principles.\r\n\r\n'),
(5776, 363, '_editorial_content_wk_5_description_e_wk', 'field_65bfc2a751893'),
