(5782, 363, '_title_ov', 'field_65c016fb49639'),
(5783, 363, 'our_values_content', '3'),
(5784, 363, '_our_values_content', 'field_65c0170c4963a'),
(5785, 363, 'our_values_content_0_title_ov', 'Different is Good'),
(5786, 363, '_our_values_content_0_title_ov', 'field_65c0172b4963b'),
(5787, 363, 'our_values_content_0_image_ov', '352'),
(5788, 363, '_our_values_content_0_image_ov', 'field_65c017394963c'),
(5789, 363, 'our_values_content_0_content_ov', 'Kyureeus aims to disrupt conventional thinking. We encourage our learners, crew, and clients to always think out-of-the-box and act boldly  it is essential for fostering innovation and identifying breakthroughs.'),
(5790, 363, '_our_values_content_0_content_ov', 'field_65c017494963d'),
(5791, 363, 'our_values_content_1_title_ov', 'Community is Everything'),
(5792, 363, '_our_values_content_1_title_ov', 'field_65c0172b4963b'),
(5793, 363, 'our_values_content_1_image_ov', '353'),
(5794, 363, '_our_values_content_1_image_ov', 'field_65c017394963c'),
(5795, 363, 'our_values_content_1_content_ov', 'Kyureeus strives to provide opportunity and upward mobility for millions of individuals worldwide. Our career-oriented training programs empower almost anyone to obtain a high-paying job in the digital workforce. '),
(5796, 363, '_our_values_content_1_content_ov', 'field_65c017494963d'),
(5797, 363, 'our_values_content_2_title_ov', 'We Courageously Make Change'),
(5798, 363, '_our_values_content_2_title_ov', 'field_65c0172b4963b'),
(5799, 363, 'our_values_content_2_image_ov', '234'),
(5800, 363, '_our_values_content_2_image_ov', 'field_65c017394963c'),
(5801, 363, 'our_values_content_2_content_ov', 'We understand that knowledge is the driving force of the Digital Age. Our programs are hand-crafted by industry leaders currently working in their respective fields, guaranteeing that our graduates receive market-ready knowledge and skills.'),
